<?php

defined('BASEPATH') OR exit('No direct script access allowed');

/**
 * Image
 *
 * @package	CodeIgniter
 * @subpackage	Rest Server
 * @category	Controller 
 */
class Picture extends CI_Controller {

    public function __construct() {
        parent::__construct();
        $this->load->model('upload_model');
    }

    public function index() {
        $request = $this->input->get();
        if (!empty($request)) {
            $ImageGUID = isset($request['ImageGUID']) ? urldecode($request['ImageGUID']) : '';
            $ThumbSize = isset($request['ThumbSize']) ? urldecode($request['ThumbSize']) : '';
            $image = $this->upload_model->get_image_by_guid($ImageGUID, $ThumbSize);
            if (!empty($image)) {
                //print_r($image);die;
                //$this->create_image(base64_decode($image['ImageData']), $image['ImageType']);
                #header('Content-Type: image/jpeg');
                #echo(base64_decode($image['ImageData']));
                $imagetype = (isset($image['ImageType'])) ? strtolower($image['ImageType']) : "";
                $ImageData = (isset($image['ImageData'])) ? base64_decode($image['ImageData']) : "";
                if ($imagetype == "jpeg" || $imagetype == "jpg") {
                    header('Content-Type: image/jpeg');
                    echo($ImageData);
                } elseif ($imagetype == "png") {
                    header('Content-Type: image/png');
                    echo($ImageData);
                } elseif ($imagetype == "gif") {
                    header('Content-Type: image/gif');
                    echo($ImageData);
                } else {
                    return false;
                }
            }else{
                $this->create_default_image($ThumbSize);
            }
        }else{
            $this->create_default_image($ThumbSize);
        }
        //echo $ImageGUID;
    }
    
    function create_default_image($ThumbSize=NULL){
        $width = $height = NULL;
        if(!empty($ThumbSize)){
            $size = explode('X', $ThumbSize);
            $width = isset($size[0]) ? $size[0] .'': NULL;
            $height = isset($size[1]) ? $size[1] .'': NULL;
        }
        //$content = '';
        //$content = '';
        $content = '';
        $this->create_image(base64_decode($content), 'jpg', $width, $height);
    }
            
    function create_image($blobcontents, $imagetype, $desired_width = null, $desired_height = null) {
        $im = imagecreatefromstring($blobcontents);
        if (!empty($desired_width) && !empty($desired_height)) {
            $new = imagecreatetruecolor($desired_width, $desired_height);
            $x = imagesx($im);
            $y = imagesy($im);
            //bg 
            //$whiteBackground = imagecolorallocate($new, 255, 255, 255); 
            $whiteBackground = imagecolorallocatealpha($new, 255, 255, 255,127); 
            imagefill ($new,0,0,$whiteBackground);
            
            imagecopyresampled($new, $im, 0, 0, 0, 0, $desired_width, $desired_height, $x, $y);
            imagedestroy($im);
        } else {
            $new = $im;
        }

        $imagetype = strtolower($imagetype);
        if ($imagetype == "jpeg" || $imagetype == "jpg") {
            header('Content-Type: image/jpeg');
            imagejpeg($new, null, 85);
        } elseif ($imagetype == "png") {
            header('Content-Type: image/png');
            imagepng($new, null, 85);
        } elseif ($imagetype == "gif") {
            header('Content-Type: image/gif');
            imagegif($new, null, 85);
        } else {
            return false;
        }
    }

}
